package com.automl.service.impl;import com.automl.data.mysql.entity.AutomlTest;import com.automl.data.mysql.mapper.TestMapper;import com.automl.entity.Test;import com.automl.service.ITestService;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @auther kayc * @date 2022/3/13 3:27 PM */@SuppressWarnings("SpringJavaInjectionPointsAutowiringInspection")@Servicepublic class TestService implements ITestService<AutomlTest> {  @Autowired  private TestMapper mapper;  @Override  public String sayHello() throws Exception {    return "hello automl";  }  public Test getById(int id) {    AutomlTest automlTest = mapper.selectById(id);    Test test = new Test();    BeanUtils.copyProperties(automlTest, test);    return test;  }}